                                Console.WriteLine("--------------------------------------------------------------------------------------------------------\r\n                                                                                                      \r\n                                                                                                      \r\n                                                                                                      \r\n                                                                                                      \r\n                                                                                                      \r\n                                                                                                      \r\n                                                                                                      \r\n                                                                                                      \r\n                                                                                                      \r\n                                                                                                      \r\n                                                                                                      \r\n                                     Такой кнопки не сущетсвует :)                                    \r\n                                                                                                      \r\n                                                                                                      \r\n                                                                                                      \r\n                                                                                                      \r\n                                                                                                      \r\n                                                                                                      \r\n                                                                                                      \r\n                                                                                                      \r\n                                                                                                      \r\n                                                                                                      \r\n---------------------------------------------------------------------------------------------------------\r\n Enter для перехода на следующее меню...");
                                Console.ReadLine();
                                Console.Clear();
                                break;
                        }
                    }
                }
            }
        }
    }
}
